import React, { useEffect, useState, useRef } from "react";
import {
  ComposedChart,
  Line,
  Area,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  Scatter,
  ResponsiveContainer,
} from 'recharts';

import { faArrowCircleRight, faArrowCircleLeft } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";



export default function CardLineChart(props) {
  const [data, setData] = useState([]);
  const [master, setMaster] = useState([]);
  //const [refAreaLeft, setRefAreaLeft] = useState('');
  //const [refAreaRight, setRefAreaRight] = useState('');
  const refAreaLeft = useRef('');
  const refAreaRight = useRef('');
  const moveRight = useRef('false');
  const zoomIn = useRef('false');
  const [refArea, setRefArea] = useState([]);
  //const [zoomIn, setZoomIn] = useState('false');
  //const [moveRight, setMoveRight] = useState('false');

  useEffect(() => {
    formatData(props.stockData);
    makeMaster(props.stockData);
  }, [props.stockData]);
  useEffect(() => {
    console.log('effect', data);
    //console.log('effect','matser',master)
    ///console.log('effect','ref',refArea)
    ///setData(data);
  },[data, master]);
  useEffect(() => {
    console.log('zoooooom')
    zoom();
  }, [refAreaRight])
  function makeMaster(dats){
    ///setMaster([]);
    const arr = [];
    for(let x=0;x<dats.length;x++){
      const ao = parseFloat(dats[x][0].split('/')[0]);
      const price = parseFloat(dats[x][4].split(':')[1]);
      const dic = {
        'ticker': dats[x][1].split(':')[1].split('/')[0],
        'ao': ((parseFloat(dats[x][0].split('/')[0]) > 10) || (parseFloat(dats[x][0].split('/')[0]) < -10)) ? parseFloat(dats[x][0].split('/')[0]) < 0 ? -5:5 : parseFloat(dats[x][0].split('/')[0]),
        'price': parseFloat(dats[x][4].split(':')[1]),
        'percent': Math.abs(Math.round(ao/price * 100000) / 1000) > 1 ? 1: Math.abs(Math.round(ao/price * 100000) / 1000) * 5
      };
      arr.push(dic)
      ///master.push(dic)
    }
    setMaster(prev => arr)
    //console.log('master', master)
  }
  function formatData(dats, _ind){
    //setData([]);
    const arr = [];
    for(let x=0;x<dats.length;x++){
      const ao = parseFloat(dats[x][0].split('/')[0]);
      const price = parseFloat(dats[x][4].split(':')[1]);
      const dic = {
        'ticker': dats[x][1].split(':')[1].split('/')[0],
        'ao': ((parseFloat(dats[x][0].split('/')[0]) > 10) || (parseFloat(dats[x][0].split('/')[0]) < -10)) ? parseFloat(dats[x][0].split('/')[0]) < 0 ? -5:5 : parseFloat(dats[x][0].split('/')[0]),
        'price': parseFloat(dats[x][4].split(':')[1]),
        'percent': Math.abs(Math.round(ao/price * 100000) / 1000) > 1 ? 1: Math.abs(Math.round(ao/price * 100000) / 1000) * 5
      };
      arr.push(dic)
      ///data.push(dic)
    }
    setData((prev) => arr)
    //console.log(data)
  };
  function zoom(){
    const arr = [];
    console.log('lefffff', refAreaLeft.current);
    console.log('riiigh', refAreaRight.current);
    for(let i=0;i<master.length;i++){
      if(master[i].ticker === refAreaRight.current || master[i].ticker === refAreaLeft.current){
        //console.log(i)
        arr.push(i)
      }
    };
    console.log('ind_arr', arr);
    if(zoomIn === 'false'){
      zoomIn.current = 'true';
    };
    const ar = master.slice(arr[0], arr[1]);
    console.log('len_slice', ar.length)
    setData(prev => ar);
    //setRefAreaLeft(prev => master[arr[0]]['ticker'])
    ///setData(prev => [...prev]);
    console.log(data);







    /*
    ///console.log('left', refAreaLeft);
    console.log('right', right);
    console.log(9999999999999, _left)
    console.log(master)
    //setData([]);
    const arr = [];
    const left = (_left !== undefined) ?_left : refAreaLeft;
    console.log('_lefffff', _left)
    for(let i=0;i<master.length;i++){
      if(master[i].ticker === right || master[i].ticker === left){
        console.log(i)
        arr.push(i)
      }
    }
    console.log(arr);
    if(moveRight === 'false'){
      setZoomIn(prev => 'true')
      const ar = master.slice(arr[0], arr[1])
      const res = [];
      for(let x=0;x<ar.length;x++){
        ///data.push(arr[x])
        res.push(ar[x]);
      }
      setData(prev => ar);
      //setRefAreaLeft(prev => master[arr[0]]['ticker'])
      ///setData(prev => [...prev]);
      console.log(data);
    };
    if(moveRight === 'true'){
      setZoomIn(prev => 'true')
      const ar = master.slice(arr[0], arr[1]);
      const datas = [...data]
      datas.push(master[arr[1]]);
      datas.pop(0);
      setData(prev => datas)
      /*
      const res = [];
      for(let x=0;x<ar.length;x++){
        ///data.push(arr[x])
        res.push(ar[x]);
      }
      setData(prev => ar);
      //setRefAreaLeft(prev => master[arr[0]]['ticker'])
      ///setData(prev => [...prev]);
      console.log(data);

    }
    */
  };
  function zoomOut(){
    zoomIn.current = 'false'; 
    formatData(props.stockData);
  };
  function panLeft(){
    if(zoomIn === 'true'){
      moveRight.current = 'true';
      while(moveRight === 'true'){
        setInterval(() => {
          console.log(2)
        }, 500);
    }
  };
}
  function panRight(){
    if(zoomIn === 'true'){
      //console.log('panr ref',data[0]['ticker']);
      //console.log('left', refAreaLeft)
      for(let i=0;i<master.length;i++){
        if(master[i]['ticker'] === refAreaLeft){
          refAreaLeft.current = master[i+1]['ticker'];
          //setRefAreaLeft(prev => master[i+1]['ticker']);
          console.log('master_left', master[i+1]['ticker']);
        };
        console.log(data[data.length-1]['ticker'], 'last')
        if(master[i]['ticker'] === refAreaRight){
          refAreaRight.current = master[i+1]
          setRefAreaRight(prev => master[i+1]['ticker'])
          console.log('right', master[i]['ticker'])
          //setMoveRight(prev => 'false')
          //console.log(2222222222222222, master[i+1]['ticker'])
        }
      };
      //console.log('new_refs', right);
      ///setRefArea(prev => new_refs)
      //zoom()
    }
  }

  useEffect(() => {
    const timer = setInterval(() => {      
      if(moveRight === 'true'){
        panRight();
      };
      if(moveRight === 'false'){
        return () => clearInterval(timer);
      };     
    }, 10);               // clearing interval
    return () => clearInterval(timer);
  }, [moveRight])

  return (
    <>
    <div style={{ width: '100%', height: '100%'}}>
      <div style={{ position: 'absolute'}}>
        <button onClick={zoomOut} type="button" className="btn update" style={{ position: 'absolute', zIndex: '1', backgroundColor: 'darkgray'}}>
            Zoom Out
        </button>
        <div style={{ height: '15vh'}}></div>
        <button onMouseEnter={(e) => moveRight && setMoveRight(prev => 'true')} onMouseLeave={() => moveRight && setMoveRight(prev => 'false')} style={{ color: 'red', position: 'absolute', zIndex: '1'}}>
          <FontAwesomeIcon icon={faArrowCircleRight} color='red' size='4x' />
        </button>
        <div style={{ height: '15vh'}}></div>
        <button style={{ color: 'red', position: 'absolute', zIndex: '1'}}>
          <FontAwesomeIcon icon={faArrowCircleLeft} color='red' size='4x' />
        </button>


      </div>
      <ResponsiveContainer width="100%" height="100%">
        <ComposedChart
          onMouseDown={(e) => setRefAreaLeft(prev => e.activeLabel)}
          // eslint-disable-next-line react/jsx-no-bind
          onMouseUp={(e) => setRefAreaRight(prev => e.activeLabel)}
          width={500}
          height={1000}
          data={data}
          style={{
            backgroundColor: '#202020',
          }}
          margin={{
            top: 20,
            right: 20,
            bottom: 20,
            left: 20,
          }}
        >
          <XAxis dataKey="ticker"  />
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar dataKey="ao" barSize={20} fill="#FF0000" />
          <Bar dataKey="percent" barSize={20} fill="#C000FF" />
          <Scatter dataKey="percent" fill="#C000FF" />
          <Scatter dataKey="price" fill="#40FF40" />
        </ComposedChart>
      </ResponsiveContainer>
      </div>

    </>
  );
}
