import React, { useEffect, useState } from "react";
import {
  ComposedChart,
  Line,
  Area,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  Scatter,
  ResponsiveContainer,
} from 'recharts';

import { faArrowCircleRight, faArrowCircleLeft } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";



export default function CardLineChart(props) {
  const [data, setData] = useState([]);
  const [refAreaLeft, setRefAreaLeft] = useState();
  const [refAreaRight, setRefAreaRight] = useState();

  useEffect(() => {
    formatData(props.stockData);
    console.log(data)
  }, [props.stockData]);
  function formatData(dats){
    setData([]);
    const arr = [];
    for(let x=0;x<dats.length;x++){
      const ao = parseFloat(dats[x][0].split('/')[0]);
      const price = parseFloat(dats[x][4].split(':')[1]);
      const dic = {
        'ticker': dats[x][1].split(':')[1].split('/')[0],
        'ao': ((parseFloat(dats[x][0].split('/')[0]) > 10) || (parseFloat(dats[x][0].split('/')[0]) < -10)) ? parseFloat(dats[x][0].split('/')[0]) < 0 ? -5:5 : parseFloat(dats[x][0].split('/')[0]),
        'price': parseFloat(dats[x][4].split(':')[1]),
        'percent': Math.abs(Math.round(ao/price * 100000) / 1000) > 1 ? 1: Math.abs(Math.round(ao/price * 100000) / 1000) * 5
      };
      arr.push(dic)
      data.push(dic)
    }
    setData(arr)
    console.log(data)
  };
  function zoom(){
    console.log(refAreaLeft);
    console.log(refAreaRight);
    const arr = [];
    for(let i=0;i<data.length;i++){
      if(data[i].ticker === refAreaRight || data[i].ticker === refAreaLeft){
        arr.push(i)
      }
    }
    console.log(arr);
    setData(data.slice(arr[0], arr[1]))
  };
  function zoomOut(){
    console.log(2)
  }
  return (
    <>
    <div style={{ width: '100%', height: '100%'}}>
      <button onClick={() => formatData(props.stockData)} type="button" className="btn update" style={{ position: 'absolute', zIndex: '1', backgroundColor: 'darkgray'}}>
          Zoom Out
      </button>
      <button  onClick={() => formatData(props.stockData)} type="button" className="btn update" style={{ position: 'absolute', zIndex: '1', backgroundColor: 'darkgray', marginTop: '10vh', height: '10vh'}}>
        <FontAwesomeIcon className="fa fa-arrow-circle-right fa-5x" icon={faArrowCircleRight} />
      </button>
      <ResponsiveContainer width="100%" height="100%">
        <ComposedChart
          onMouseDown={(e) => setRefAreaLeft(e.activeLabel)}
          onMouseMove={(e) => refAreaLeft && setRefAreaRight(e.activeLabel)}
          // eslint-disable-next-line react/jsx-no-bind
          onMouseUp={zoom}
          width={500}
          height={1000}
          data={data}
          style={{
            backgroundColor: '#202020',
          }}
          margin={{
            top: 20,
            right: 20,
            bottom: 20,
            left: 20,
          }}
        >
          <XAxis dataKey="ticker"  />
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar dataKey="ao" barSize={20} fill="#FF0000" />
          <Bar dataKey="percent" barSize={20} fill="#C000FF" />
          <Scatter dataKey="percent" fill="#C000FF" />
          <Scatter dataKey="price" fill="#40FF40" />
        </ComposedChart>
      </ResponsiveContainer>
      </div>

    </>
  );
}
