import { ADD_ARTICLE } from "../constants/action-types";

const initialState = {
  stock: ''
};


function rootReducer(state = initialState, action) {
  if (action.type === ADD_STOCK) {
    return {...state, stock: prev => action.payload}
  }
  return state;
}

export default rootReducer;
