import React, { useReducer, useEffect} from "react";
import { BarChart, Bar, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { connect } from "react-redux";
import { addBarData } from "../js/actions/index";


const mapStateToProps = state => {
  return { 
    masterData: state.masterHistoryData,
    data: state.cardBarData,
   };
};

const initialState = {
  master: [],
  masterData: [],
  data: [],
  index: [12,5],
};
const reducer = (state, action) => {
  switch (action.type) {
    case 'set': return {...state, master: action.arr};
    case 'setData': return {...state, data: action.arr}
    case 'setMasterData': return {...state, masterData: action.arr};
    case 'panright': return {...state, index: action.arr};
    case 'panleft': return {...state, index: action.arr};
    default: throw new Error('Unexpected action');
  }
};

function ConnectedCardBarChart(props) {
  const [state, dispatch] = useReducer(reducer, initialState);
  /*
  useEffect(() => {
    if(props.historyData.length > 1){
      formatData();
    }
  }, [props.historyData]);
  */
  useEffect(() => {
    if(props.masterData.length > 1){
      formatData()
    }
  }, [props.masterData])
  useEffect(() => {
    console.log('index change', state.index)
  }, [state.index])
  function formatData(){
    const sl_range = [props.masterData.length-state.index[0], props.masterData.length-state.index[1]];
    console.log('sl_range', sl_range)
    props.addBarData(props.masterData.slice(sl_range[0], sl_range[1]))
    dispatch({ type: 'setData', arr: props.masterData.slice(sl_range[0], sl_range[1])});
    dispatch({ type: 'setMasterData', arr: props.masterData.slice(sl_range[0], sl_range[1])});
    //dispatch({ type: 'set', arr: props.masterData});


  };
  function panRight(){
    const len = state.masterData.length;
    console.log('len', len)
    if(state.index[0] > 8){
      dispatch({ type: 'panright', arr: [state.index[0]-1, state.index[1] - 1]});
      formatData();
    };
  };
  function panLeft(){
    const len = state.masterData.length;
    console.log('len', len)
    if(state.index[0] < props.masterData.length){
      dispatch({ type: 'panleft', arr: [state.index[0]+1, state.index[1] + 1]});
      formatData();
    };
  };
  return (
    <>
      <button onClick={panRight} style={{ height: '2vw', width: '2vw', position: 'absolute', zIndex: '1'}}>right</button>
      <button onClick={panLeft} style={{ marginTop: '2vw' ,height: '2vw', width: '2vw', position: 'absolute', zIndex: '1'}}>left</button>
      <ResponsiveContainer width="100%" height="100%" style={{ backgroundColor: 'white'}}>
        <BarChart
          width={500}
          height={300}
          data={state.data}
          style={{ backgroundColor: 'white'}}
          margin={{
            top: 5,
            right: 30,
            left: 20,
            bottom: 5,
          }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="date" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar dataKey="price" fill="black" />
          <Bar dataKey="percent" fill="green" />
          <Bar dataKey="ao" fill="blue" />
        </BarChart>
      </ResponsiveContainer>
    </>
  );
}

const CardBarChart = connect(
  mapStateToProps,
  { addBarData }
  )(ConnectedCardBarChart);

export default CardBarChart;
