import React, { useReducer, useEffect} from "react";
import { BarChart, Bar, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const data = [
  {
    name: 'Page A',
    uv: 4000,
    pv: 2400,
    amt: 2400,
  },
  {
    name: 'Page B',
    uv: 3000,
    pv: 1398,
    amt: 2210,
  },
  {
    name: 'Page C',
    uv: 2000,
    pv: 9800,
    amt: 2290,
  },
  {
    name: 'Page D',
    uv: 2780,
    pv: 3908,
    amt: 2000,
  },
  {
    name: 'Page E',
    uv: 1890,
    pv: 4800,
    amt: 2181,
  },
  {
    name: 'Page F',
    uv: 2390,
    pv: 3800,
    amt: 2500,
  },
  {
    name: 'Page G',
    uv: 3490,
    pv: 4300,
    amt: 2100,
  },
];


const initialState = {
  master: [],
  masterData: [],
  data: [],
};
const reducer = (state, action) => {
switch (action.type) {
  case 'set': return {...state, master: action.arr};
  case 'setData': return {...state, data: action.arr}
  case 'setMasterData': return {...state, masterData: action.arr};
  default: throw new Error('Unexpected action');
}
};

export default function CardBarChart(props) {
  const [state, dispatch] = useReducer(reducer, initialState);
  useEffect(() => {
    if(props.historyData.length > 1){
      formatData();
    }
  }, [props.historyData])
  function formatData(){
    const sl_range = [props.historyData.length-8, props.historyData.length-1];
    dispatch({ type: 'setData', arr: props.historyData.slice(sl_range[0], sl_range[1])});
    dispatch({ type: 'setMasterData', arr: props.historyData.slice(sl_range[0], sl_range[1])});
    dispatch({ type: 'set', arr: props.historyData});


  }
  return (
    <>
      <button style={{ height: '10vw', width: '10vw'}}>hello</button>
      <ResponsiveContainer width="100%" height="100%" style={{ backgroundColor: 'white'}}>
        <BarChart
          width={500}
          height={300}
          data={state.data}
          style={{ backgroundColor: 'white'}}
          margin={{
            top: 5,
            right: 30,
            left: 20,
            bottom: 5,
          }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="date" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar dataKey="price" fill="black" />
          <Bar dataKey="percent" fill="green" />
          <Bar dataKey="ao" fill="blue" />
        </BarChart>
      </ResponsiveContainer>
    </>
  );
}
