import React, { useEffect, useState } from "react";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

export default function CardLineChart(props) {
  const [data, setData] = useState();
  useEffect(() => {
    formatData(props.stockData);
    console.log(data)
  }, [props.stockData]);
  function formatData(dats){
    setData([]);
    const arr = [];
    for(let x=0;x<dats.length;x++){
      const ao = parseFloat(dats[x][0].split('/')[0]);
      const price = parseFloat(dats[x][4].split(':')[1]);
      const dic = {
        'ticker': dats[x][1].split(':')[1].split('/')[0],
        'ao': parseFloat(dats[x][0].split('/')[0]),
        'price': parseFloat(dats[x][4].split(':')[1]),
        'percent': Math.round(ao/price * 100000) / 1000
      };
      console.log(dic)
      arr.push(dic)
      setData(prev => [...prev, dic])
    }
    setData()
    console.log(data)
  }
  return (
    <>
      <ResponsiveContainer width="100%" height="100%">
        <LineChart
          width={500}
          height={300}
          data={data}
          margin={{
            top: 5,
            right: 30,
            left: 20,
            bottom: 5,
          }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="name" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Line type="monotone" dataKey="pv" stroke="#8884d8" activeDot={{ r: 8 }} />
          <Line type="monotone" dataKey="uv" stroke="#82ca9d" />
        </LineChart>
      </ResponsiveContainer>
    </>
  );
}
