import React, { useContext, useState, useEffect } from "react";
import dataContext from "../gData";
import '../App.css';

// components

export default function CardSocialTraffic(props) {
  const [data, setData] = useState([]);
  const value = useContext(dataContext);
  useEffect(() => {
    formatData(props.stockData);
    console.log(data)
  }, [props.stockData]);
  function formatData(dats){
    for (let x=0;x<dats.length;x++){
      const ao = parseFloat(dats[x][0].split('/')[0]);
      const tick = dats[x][1].split(':')[1].split('/')[0];
      const price = dats[x][4].split(':')[1];

      const ar = [tick, ao, price];
      setData(prev => [...prev, ar])
    }
  };
  function reverseArr(){
    const revArr = data.reverse();
    setData([]);
    ///const revArr = props.stockData.reverse();
    for(let x=0;x<revArr.length;x++){
      /*const ao = parseFloat(revArr[x][0].split('/')[0]);
      const tick = revArr[x][1].split(':')[1].split('/')[0];
      const price = revArr[x][4].split(':')[1];

      const ar = [tick, ao, price];*/
      setData(prev => [...prev, revArr[x]])
    }
    console.log(revArr);
  }
  return (
    <>
      <div className="relative flex flex-col min-w-0 break-words bg-white w-full mb-6 shadow-lg rounded">
        <div className="rounded-t mb-0 px-4 py-3 border-0">
          <div className="flex flex-wrap items-center">
            <div className="relative w-full px-4 max-w-full flex-grow flex-1">
              <h3 className="font-semibold text-base text-gray-800">
                Social traffic
              </h3>
            </div>
            <div className="relative w-full px-4 max-w-full flex-grow flex-1 text-right">
              <button
                onClick={reverseArr}
                className="bg-indigo-500 text-white active:bg-indigo-600 text-xs font-bold uppercase px-3 py-1 rounded outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150"
                type="button"
              >
                Reverse
              </button>
              <button
                oncli
                className="bg-red-500 text-white active:bg-red-600 text-xs font-bold uppercase px-3 py-1 rounded outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150"
                type="button"
              >
                Percent
              </button>
            </div>
          </div>
        </div>
        <div className="block w-full overflow-x-auto">
          {/* Projects table */}
          <table className="items-center w-full bg-transparent border-collapse">
            <thead className="thead-light">
              <tr>
                <th className="px-6 bg-gray-100 text-gray-600 align-middle border border-solid border-gray-200 py-3 text-xs uppercase border-l-0 border-r-0 whitespace-no-wrap font-semibold text-left">
                  Ticker
                </th>
                <th className="px-6 bg-gray-100 text-gray-600 align-middle border border-solid border-gray-200 py-3 text-xs uppercase border-l-0 border-r-0 whitespace-no-wrap font-semibold text-left">
                  AO
                </th>
                <th className="px-6 bg-gray-100 text-gray-600 align-middle border border-solid border-gray-200 py-3 text-xs uppercase border-l-0 border-r-0 whitespace-no-wrap font-semibold text-left min-w-140-px">
                  AO vs Price
                </th>
              </tr>
            </thead>
            <tbody>
            {data.map((item, index) => (
                <tr key={`${item[0]}` + `${index}`} >
                  <th className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-no-wrap p-4 text-left">
                    {item[0]}
                  </th>
                  <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-no-wrap p-4">
                    {Math.round(item[1] * 1000) / 1000}
                  </td>
                  <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-no-wrap p-4">
                    <div className="flex items-center">
                      <span className="mr-2">{Math.round((item[1]/ item[2]) * 100000) / 1000}%</span>
                      <div className="relative w-full">
                        <div className="overflow-hidden h-2 text-xs flex rounded bg-orange-200">
                          <div
                            style={{ width: `${parseInt(Math.abs(Math.round((item[1]/ item[2] * 100))))}%` }}
                            className={`shadow-none flex flex-col text-center whitespace-nowrap text-white justify-center ${(item[1] > 0) ? 'bg-green-500': 'bg-red-500'}`}
                        ></div>
                      </div>
                      </div>
                    </div>
                  </td>
                </tr>

                ))}

            </tbody>
          </table>
        </div>
      </div>
    </>
  );
}
