import React, { useEffect, useState, useReducer } from "react";
import axios from 'axios';


const initialState = {
    master: [],
    masterData: [],
};
const reducer = (state, action) => {
  switch (action.type) {
    case 'set': return {...state, master: action.arr};
    case 'setData': return {...state, masterData: action.arr};
    case 'increment': return state + 1;
    case 'decrement': return state - 1;
    case 'reset': return 0;
    default: throw new Error('Unexpected action');
  }
};

export default function CardHistoryChart(props){
    const [state, dispatch] = useReducer(reducer, initialState);
    useEffect(() => {
        if(state.master.length > 1){
            formatData();
        }
    }, [state])
    useEffect(() => {
        const arr = [];
        if(props.stock !== ''){
            console.log(props.stock.split(' ')[2])
            getData()
        };

      }, [props.stock]);
      const getData = () => {  
        axios.get(`http://localhost:5000/${props.stock.split(' ')[2]}`, {
          headers: {
            "Access-Control-Allow-Origin": "*",
          }
        })  
        .then((res) => {  
          dispatch({ type: 'set', arr: res.data});
          ///setStockData(res.data);
          //console.log(stockData) 
        })
        .catch(err => {  
          console.log(err)  
        });  
      };
      function formatData(){
        const arr = [];
        console.log(state.master)
        for(let i=0;i<state.master[1].length;i++){
            //console.log(state.master[1][i]);
            const price = state.master[1][i];
            const ao = state.master[2][i];
            const date = state.master[3][i];
            const percent = price / ao;
            console.log(percent);
            const dic = {'name': state.master[0], 'price': price, 'ao': ao, 'date': date};
            arr.push(dic);
        };

      };

    return (
        <>
            <div>{props.stock}</div>
        </>
    )
}
